INSERT INTO `Content` (`id`, `name`, `html`, `less`, `css`, `es6`, `js`, `ip_address`, `saved`, `last_update`) VALUES
(1,	'Tweakable Spirograph',	'<html lang=\"en\">\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <title>Browser Spirograph</title>\r\n    <script src=\"https://code.jquery.com/jquery-3.1.1.slim.min.js\" integrity=\"sha256-/SIrNqv8h6QGKDuNoLGA4iret+kyesCkHGzVUUV0shc=\" crossorigin=\"anonymous\"></script>\r\n    <link href=\"https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css\" rel=\"stylesheet\" integrity=\"sha384-BVYiiSIFeK1dGmJRAkycuHAHRg32OmUcww7on3RYdg4Va+PmSTsz/K68vbdEjh4u\" crossorigin=\"anonymous\">\r\n    <script src=\"https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js\" integrity=\"sha384-Tc5IQib027qvyjSMfHjOMaLkfuWVxZxUPnCJA7l2mCWNIpG9mGCD8wGNIcPD7Txa\" crossorigin=\"anonymous\"></script>\r\n</head>\r\n<body>\r\n<div class=\"col\">\r\n    <div class=\"col-md-9\">\r\n        <canvas id=\"c\"></canvas>\r\n    </div>\r\n\r\n    <div class=\"col-md-3\">\r\n\r\n        <h1>Spirograph (kinda)</h1>\r\n        <hr>\r\n\r\n        <div id=\"controls\">\r\n        <input type=\"button\" value=\"Reset\" class=\"btn btn-danger\" onclick=\"currentPreset = -1;nextPreset();init()\">\r\n        <input id=\"circles-btn\" type=\"button\" value=\"Hide circles\" class=\"btn btn-default\" onclick=\"showHideCircles()\">\r\n            <hr>\r\n        <input type=\"button\" value=\"Next preset\" class=\"btn btn-primary\" onclick=\"nextPreset();init()\">\r\n        <input type=\"button\" value=\"Random\" class=\"btn btn-default\" onclick=\"randomParams();init()\">\r\n            <hr>\r\n        <input id=\"speedminus\" type=\"button\" value=\"Speed -\" class=\"btn btn-default\" onclick=\"halfSpeed()\">\r\n        <input id=\"speedplus\" type=\"button\" value=\"Speed +\" class=\"btn btn-default\" onclick=\"doubleSpeed()\">\r\n        </div>\r\n<hr>\r\n\r\n        <a data-toggle=\"collapse\" data-target=\"#controls-more\">\r\n            <span class=\"glyphicon glyphicon-menu-down\"></span>\r\n            Show advanced\r\n            <span class=\"glyphicon glyphicon-menu-down\"></span>\r\n        </a>\r\n<hr>\r\n        <div id=\"controls-more\" class=\"collapse\">\r\n\r\n            <div class=\"form-group\">\r\n            <label for=\"ncircles\">Circles :</label>\r\n            <div class=\"input-group\">\r\n                <span class=\"input-group-btn\">\r\n                    <button id=\"cminus\" class=\"btn btn-default\" type=\"button\"><span class=\"glyphicon glyphicon-minus-sign\"></span> </button>\r\n                </span>\r\n\r\n                <input type=\"text\" class=\"form-control text-center\" id=\"ncircles\" value=\"5\">\r\n\r\n                <span class=\"input-group-btn\">\r\n                    <button id=\"cplus\" class=\"btn btn-default\" type=\"button\"><span class=\"glyphicon glyphicon-plus-sign\"></span> </button>\r\n                </span>\r\n            </div>\r\n        </div>\r\n\r\n        <div class=\"form-group\">\r\n            <label for=\"ratio\">Ratio :</label>\r\n            <div class=\"input-group\">\r\n                <span class=\"input-group-btn\">\r\n                    <button id=\"rminus\" class=\"btn btn-default\" type=\"button\"><span class=\"glyphicon glyphicon-minus-sign\"></span> </button>\r\n                </span>\r\n\r\n                <input type=\"text\" class=\"form-control text-center\" id=\"ratio\" value=\"2\">\r\n\r\n                <span class=\"input-group-btn\">\r\n                    <button id=\"rplus\" class=\"btn btn-default\" type=\"button\"><span class=\"glyphicon glyphicon-plus-sign\"></span> </button>\r\n                </span>\r\n            </div>\r\n        </div>\r\n\r\n        <div class=\"form-group\">\r\n            <label for=\"pow\">Pow :</label>\r\n            <div class=\"input-group\">\r\n                <span class=\"input-group-btn\">\r\n                    <button id=\"pminus\" class=\"btn btn-default\" type=\"button\"><span class=\"glyphicon glyphicon-minus-sign\"></span> </button>\r\n                </span>\r\n\r\n                <input type=\"text\" class=\"form-control text-center\" id=\"pow\" value=\"-1.5\">\r\n\r\n                <span class=\"input-group-btn\">\r\n                    <button id=\"pplus\" class=\"btn btn-default\" type=\"button\"><span class=\"glyphicon glyphicon-plus-sign\"></span> </button>\r\n                </span>\r\n            </div>\r\n        </div>\r\n</div>\r\n\r\n\r\n    </div>\r\n    <div class=\"col-md-9\" style=\"margin-top: 30px\">\r\n    <span><a href=\"http://pjoy.fr/en\">PJoy</a> |\r\n        More stuff :\r\n    <a href=\"http://pjoy.fr/pokemon/\">1</a>\r\n    <a href=\"http://pjoy.fr/tree/\">2</a>\r\n    <a href=\"http://pjoy.fr/files/TDPhysicsV2/\">3</a>\r\n    <a href=\"http://pjoy.fr/Tiles/\">4</a>\r\n    <a href=\"http://pjoy.fr/ruby/\">5</a>\r\n    <a href=\"http://pjoy.fr/files/TDPhysics/\">6</a>\r\n    <a href=\"http://pjoy.fr/CANONS/V2/\">7</a></span>\r\n    </div>\r\n</div>\r\n</body>\r\n</html>',	'h1 { color: red; } i { color: green; }',	'html {\r\n    background: white;\r\n    text-align: center;\r\n}\r\n\r\n#controls-more {\r\n    text-align: left;\r\n}\r\n\r\ncanvas {\r\n    background: white;\r\n}\r\n\r\nimg {\r\n    display: none;\r\n}\r\n\r\nbutton {\r\n    outline: none;\r\n}\r\n\r\na {\r\n    cursor: pointer;\r\n    text-align: center;\r\n}',	'console.log(\'test\');',	'var canvas,\r\n    ctx,\r\n    boundx = Math.min(window.innerWidth,window.innerHeight),\r\n    boundy = Math.min(window.innerWidth,window.innerHeight),\r\n    anim;\r\n\r\nwindow.requestAnimFrame =\r\n    window.requestAnimationFrame ||\r\n    window.webkitRequestAnimationFrame ||\r\n    window.mozRequestAnimationFrame ||\r\n    window.oRequestAnimationFrame ||\r\n    window.msRequestAnimationFrame ||\r\n    function (callback) {\r\n        window.setTimeout(callback, 1000 / 60);\r\n    };\r\n\r\nvar nCircles,\r\n    ratio,\r\n    pow,\r\n    speed,\r\n    drawCircles = true;\r\n\r\nvar ratios = [0.9,1.1,1.25,1.5,1.75,2,2.5,3,5];\r\nvar pows = [-9,-6,-3,-2.5,-2,-1.5,-1,1,1.5,2,2.5,3,6,9];\r\n\r\nvar presets = [[6,3,-3],[5,3,-8],[5,0.9,-3],[6,1.1,-3],[4,3,3.5],[4,3,-3.5],[4,2,-4.5],[5,2,-1.5],[6,1.5,-2],[6,1.5,-1.01],[3,1.1,-2.01],[10,2,-1.5],[6,2.5,-6],[6,3,-9]];\r\nvar currentPreset = 0;\r\n\r\nfunction indexOfClosest(arr, x) {\r\n    var tmp = arr.slice();\r\n    var val = tmp.sort( (a, b) => Math.abs(x - a) - Math.abs(x - b) )[0];\r\n    return arr.indexOf(val);\r\n}\r\n\r\nfunction showHideCircles() {\r\n    drawCircles = !drawCircles;\r\n    if (drawCircles) jQuery(\'#circles-btn\').val(\'Hide circles\');\r\n    else jQuery(\'#circles-btn\').val(\'Show Circles\')\r\n}\r\n\r\nwindow.onload = function(){\r\n    //controls\r\n    jQuery(\'#cminus\').click(function() {\r\n        jQuery(\'#ncircles\').val(function (i, v) {\r\n            jQuery(\'#cplus\').prop(\"disabled\", false);\r\n            var val = parseInt(v);\r\n            if (val > 2) {\r\n                if (val==3) jQuery(\'#cminus\').prop(\"disabled\", true);\r\n                return val - 1\r\n            }\r\n        });\r\n        init()\r\n    });\r\n\r\n    jQuery(\'#cplus\').click(function() {\r\n        jQuery(\'#cminus\').prop(\"disabled\", false);\r\n        jQuery(\'#ncircles\').val(function (i, v) {\r\n            var val = parseInt(v);\r\n            if (val < 10) {\r\n                if (val==9) jQuery(\'#cplus\').prop(\"disabled\", true);\r\n                return val + 1\r\n            }\r\n        });\r\n        init()\r\n    });\r\n\r\n    jQuery(\'#rminus\').click(function() {\r\n        jQuery(\'#ratio\').val(function (i, v) {\r\n            jQuery(\'#rplus\').prop(\"disabled\", false);\r\n            var val = parseFloat(v);\r\n            var next = indexOfClosest(ratios,val)-1;\r\n            if (next == 0) jQuery(\'#rminus\').prop(\"disabled\", true);\r\n            return ratios[next];\r\n        });\r\n        init()\r\n    });\r\n\r\n    jQuery(\'#rplus\').click(function() {\r\n        jQuery(\'#ratio\').val(function (i, v) {\r\n            jQuery(\'#rminus\').prop(\"disabled\", false);\r\n            var val = parseFloat(v);\r\n            var next = indexOfClosest(ratios,val)+1;\r\n            if (next == ratios.length-1) jQuery(\'#rplus\').prop(\"disabled\", true);\r\n            return ratios[next];\r\n        });\r\n        init()\r\n    });\r\n\r\n    jQuery(\'#pminus\').click(function() {\r\n        jQuery(\'#pow\').val(function (i, v) {\r\n            jQuery(\'#pplus\').prop(\"disabled\", false);\r\n            var val = parseFloat(v);\r\n            var next = indexOfClosest(pows,val)-1;\r\n            if (next == 0) jQuery(\'#pminus\').prop(\"disabled\", true);\r\n            return pows[next];\r\n        });\r\n        init()\r\n    });\r\n\r\n    jQuery(\'#pplus\').click(function() {\r\n        jQuery(\'#pow\').val(function (i, v) {\r\n            jQuery(\'#pminus\').prop(\"disabled\", false);\r\n            var val = parseFloat(v);\r\n            var next = indexOfClosest(pows,val)+1;\r\n            if (next == pows.length-1) jQuery(\'#pplus\').prop(\"disabled\", true);\r\n            return pows[next];\r\n        });\r\n        init()\r\n    });\r\n\r\n    canvas = document.getElementById(\'c\');\r\n    ctx = canvas.getContext(\'2d\');\r\n    canvas.width = boundx;\r\n    canvas.height = boundy;\r\n    init();\r\n};\r\n\r\nfunction nextPreset() {\r\n    currentPreset++;\r\n    var pres = presets[currentPreset%presets.length];\r\n    jQuery(\'#ncircles\').val(pres[0]);\r\n    jQuery(\'#ratio\').val(pres[1]);\r\n    jQuery(\'#pow\').val(pres[2]);\r\n}\r\n\r\nfunction randomParams(){\r\n    var nc = Math.round(Math.random()*8+3);\r\n    var r = Math.round((Math.random()*4.2+0.8)*10)/10;\r\n    if (r == 1) {\r\n        randomParams();return;\r\n    }\r\n    var p = Math.random()*12-6;\r\n    if (Math.random() > 0.5 && p > 0) {\r\n        p *= -1;\r\n    }\r\n    if (Math.random() < 0.75){\r\n        p = Math.round(p)\r\n    } else {\r\n        p = Math.round(p*100)/100;\r\n    }\r\n\r\n    jQuery(\'#ncircles\').val(nc);\r\n    jQuery(\'#ratio\').val(r);\r\n    jQuery(\'#pow\').val(p);\r\n}\r\n\r\nfunction init() {\r\n    jQuery(\'#speedminus\').prop(\"disabled\", false);\r\n    jQuery(\'#speedplus\').prop(\"disabled\", false);\r\n\r\n\r\n    nCircles = jQuery(\'#ncircles\').val();\r\n    ratio = jQuery(\'#ratio\').val();\r\n    pow = jQuery(\'#pow\').val();\r\n\r\n    window.cancelAnimationFrame(anim);\r\n    speed = computeSpeed();\r\n\r\n    logs = [];\r\n    for (var i = 0 ; i < nCircles; i ++){\r\n        var nlog = new log();\r\n        logs.push(nlog);\r\n    }\r\n    e = new ensemble(0.2);\r\n    e.init(nCircles);\r\n\r\n    e = new ensemble(speed);\r\n    e.init(nCircles);\r\n    e.render();\r\n    update();\r\n}\r\n\r\nfunction doubleSpeed(){\r\n    e.angle *= 2;\r\n    if (e.angle > 0.01) jQuery(\'#speedplus\').prop(\"disabled\", true);\r\n    if (e.angle > 0.0005 ) jQuery(\'#speedminus\').prop(\"disabled\", false);\r\n}\r\n\r\nfunction halfSpeed(){\r\n    e.angle /= 2;\r\n    if (e.angle < 0.01) jQuery(\'#speedplus\').prop(\"disabled\", false);\r\n    if (e.angle < 0.0005 ) jQuery(\'#speedminus\').prop(\"disabled\", true);\r\n}\r\n\r\nfunction computeSpeed() {\r\n    logs = [];\r\n    for (var i = 0 ; i < nCircles; i ++){\r\n        var nlog = new log();\r\n        logs.push(nlog);\r\n    }\r\n    e = new ensemble(0.2);\r\n    e.init(nCircles);\r\n    e.update();\r\n    e.update();\r\n\r\n    var x1 = logs[nCircles-1].vals[0].x;\r\n    var y1 = logs[nCircles-1].vals[0].y;\r\n    var x2 = logs[nCircles-1].vals[1].x;\r\n    var y2 = logs[nCircles-1].vals[1].y;\r\n\r\n    var d = Math.sqrt((x2-x1)*(x2-x1)+(y2-y1)*(y2-y1));\r\n\r\n    return 1/d/2;\r\n}\r\n\r\nfunction update(){\r\n    e.update();\r\n    e.render();\r\n    anim = requestAnimFrame(update);\r\n}\r\n\r\nvar circle = function (parent,r) {\r\n    this.parent = parent;\r\n    this.r = r;\r\n    this.x = this.parent.x;\r\n    this.y = this.parent.y - this.parent.r + this.r;\r\n    this.relativeAngle = 3/2*Math.PI;\r\n};\r\n\r\ncircle.prototype.draw = function () {\r\n    ctx.beginPath();\r\n    ctx.arc(this.x, this.y, this.r, 0, 2 * Math.PI);\r\n    ctx.stroke();\r\n};\r\n\r\ncircle.prototype.rollRight = function (angle) {\r\n    this.relativeAngle += angle;\r\n    this.x = this.parent.x + (this.parent.r - this.r) *Math.cos(this.relativeAngle);\r\n    this.y = this.parent.y + (this.parent.r - this.r) *Math.sin(this.relativeAngle);\r\n};\r\n\r\nvar ensemble = function (angle) {\r\n    this.angle = angle*Math.PI/180;\r\n    this.circles = []\r\n};\r\n\r\nensemble.prototype.init = function(n) {\r\n    var firstCircle = new circle({x:boundx/2,y:boundy/2,r:boundx/2},boundx/2);\r\n    this.circles.push(firstCircle);\r\n    for ( var i = 1; i < n; i++ ){\r\n        var c = new circle(this.circles[i-1],this.circles[i-1].r/ratio);\r\n        this.circles.push(c);\r\n    }\r\n};\r\n\r\nensemble.prototype.update = function () {\r\n    var a = this.angle;\r\n    var max = this.circles.length;\r\n    this.circles.forEach(function (c,x) {\r\n        c.rollRight(\r\n            a\r\n            //* Math.pow(pow,x%3+1)\r\n            * Math.pow(pow,x)\r\n        );\r\n        logs[x].vals.push({x:c.x,y:c.y});\r\n    })\r\n};\r\n\r\nensemble.prototype.render =function () {\r\n    ctx.clearRect(0,0,boundx,boundy);\r\n    this.circles.forEach(function(c){\r\n        c.draw();\r\n    });\r\n\r\n    for (var i = 1; i<nCircles-1; i++) {\r\n        logs[i].draw(\'rgba(255,0,0,\'+i/nCircles/1.5+\')\');\r\n    }\r\n    logs[nCircles-1].draw(\'#315998\');\r\n\r\n};\r\n\r\nvar log = function () {\r\n    this.vals = [];\r\n};\r\n\r\nlog.prototype.draw = function(color) {\r\n    var max = this.vals.length-1;\r\n    if (max < 0) return;\r\n    ctx.strokeStyle=color;\r\n    ctx.beginPath();\r\n    ctx.moveTo(this.vals[max].x,this.vals[max].y);\r\n    for (var i = 1; i < max; i ++){\r\n        if ((max-i) < 0) break;\r\n        ctx.lineTo(this.vals[max-i].x,this.vals[max-i].y);\r\n    }\r\n    ctx.stroke();\r\n     if (drawCircles) ctx.strokeStyle=\'rgba(0,0,0,1)\';\r\n    else ctx.strokeStyle=\'rgba(0,0,0,0)\';\r\n};',	'::ffff:127.0.0.1',	1,	'2017-01-11 05:09:43'),
(3,	'KaWaJO',	'<div class=\"container\">\r\n    <!-- This checkbox will give us the toggle behavior, it will be hidden, but functional -->\r\n    <input id=\"toggle\" type=\"checkbox\">\r\n\r\n    <!-- IMPORTANT: Any element that we want to modify when the checkbox state changes go here, being \"sibling\" of the checkbox element -->\r\n\r\n    <!-- This label is tied to the checkbox, and it will contain the toggle \"buttons\" -->\r\n    <label class=\"toggle-container\" for=\"toggle\">\r\n        <!-- If menu is open, it will be the \"X\" icon, otherwise just a clickable area behind the hamburger menu icon -->\r\n        <span class=\"button button-toggle\"></span>\r\n    </label>\r\n\r\n    <!-- The nav menu -->\r\n    <nav class=\"nav\">\r\n        <a class=\"nav-item\" href=\"\">Dashboard</a>\r\n        <a class=\"nav-item\" href=\"\">History</a>\r\n        <a class=\"nav-item\" href=\"\">Statistics</a>\r\n        <a class=\"nav-item\" href=\"\">Settings</a>\r\n    </nav>\r\n\r\n    <!-- Just dummy content like in the animated gif -->\r\n    <section class=\"dummy-content\">\r\n        <div class=\"circle\"></div>\r\n        <div class=\"text\">\r\n            <span></span><span></span>\r\n        </div>\r\n        <div class=\"square-top\"></div>\r\n        <div class=\"square-behind\"></div>\r\n    </section>\r\n</div>',	'',	'/* Basic styles */\r\n* {\r\n  box-sizing: border-box;\r\n}\r\n\r\nhtml, body {\r\n  margin: 0;\r\n}\r\n\r\nbody {\r\n  font-family: sans-serif;\r\n  background-color: #F6C390;\r\n}\r\n\r\na {\r\n  text-decoration: none;\r\n}\r\n\r\n.container {\r\n  position: relative;\r\n  margin: 35px auto 0;\r\n  width: 300px;\r\n  height: 534px;\r\n  background-color: #533557;\r\n  overflow: hidden;\r\n}\r\n\r\n/* Toggle functionality */\r\n#toggle {\r\n  display: none;\r\n}\r\n\r\n#toggle:checked ~ .toggle-container .button-toggle {\r\n  box-shadow: 0 0 0 550px rgba(0, 0, 0, 0.1), inset 0 0 0 20px rgba(0, 0, 0, 0.1);\r\n}\r\n#toggle:checked ~ .toggle-container .button-toggle:before {\r\n  transform: translateY(-50%) rotate(45deg) scale(1);\r\n}\r\n#toggle:checked ~ .toggle-container .button-toggle:after {\r\n  transform: translateY(-50%) rotate(-45deg) scale(1);\r\n}\r\n#toggle:checked ~ .nav {\r\n  margin-bottom: 100px;\r\n  pointer-events: auto;\r\n  transform: translate(50px, 50px);\r\n}\r\n#toggle:checked ~ .nav .nav-item {\r\n  color: #EC7263;\r\n  letter-spacing: 0;\r\n  height: 40px;\r\n  line-height: 40px;\r\n  margin-top: 0;\r\n  opacity: 1;\r\n  transform: scaleY(1);\r\n  transition: 0.5s, opacity 0.1s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(1) {\r\n  transition-delay: 0.15s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(1):before {\r\n  transition-delay: 0.15s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(2) {\r\n  transition-delay: 0.1s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(2):before {\r\n  transition-delay: 0.1s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(3) {\r\n  transition-delay: 0.05s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(3):before {\r\n  transition-delay: 0.05s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(4) {\r\n  transition-delay: 0s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:nth-child(4):before {\r\n  transition-delay: 0s;\r\n}\r\n#toggle:checked ~ .nav .nav-item:before {\r\n  opacity: 0;\r\n}\r\n#toggle:checked ~ .dummy-content {\r\n  padding-top: 30px;\r\n}\r\n#toggle:checked ~ .dummy-content:before {\r\n  background-color: rgba(0, 0, 0, 0.3);\r\n}\r\n\r\n/* Toggle button */\r\n.button-toggle {\r\n  position: absolute;\r\n  display: inline-block;\r\n  width: 20px;\r\n  height: 20px;\r\n  margin: 25px;\r\n  background-color: transparent;\r\n  border: none;\r\n  cursor: pointer;\r\n  border-radius: 100%;\r\n  transition: 0.6s;\r\n}\r\n.button-toggle:hover {\r\n  box-shadow: 0 0 0 8px rgba(0, 0, 0, 0.1), inset 0 0 0 20px rgba(0, 0, 0, 0.1);\r\n}\r\n.button-toggle:before, .button-toggle:after {\r\n  position: absolute;\r\n  content: \'\';\r\n  top: 50%;\r\n  left: 0;\r\n  width: 100%;\r\n  height: 2px;\r\n  background-color: #EC7263;\r\n  border-radius: 5px;\r\n  transition: 0.5s;\r\n}\r\n.button-toggle:before {\r\n  transform: translateY(-50%) rotate(45deg) scale(0);\r\n}\r\n.button-toggle:after {\r\n  transform: translateY(-50%) rotate(-45deg) scale(0);\r\n}\r\n\r\n/* Menu */\r\n.nav {\r\n  display: inline-block;\r\n  margin: 25px 25px 20px;\r\n  pointer-events: none;\r\n  transition: 0.5s;\r\n}\r\n\r\n.nav-item {\r\n  position: relative;\r\n  display: inline-block;\r\n  float: left;\r\n  clear: both;\r\n  color: transparent;\r\n  font-size: 14px;\r\n  letter-spacing: -6.2px;\r\n  height: 7px;\r\n  line-height: 7px;\r\n  text-transform: uppercase;\r\n  white-space: nowrap;\r\n  transform: scaleY(0.2);\r\n  transition: 0.5s, opacity 1s;\r\n}\r\n.nav-item:nth-child(1) {\r\n  transition-delay: 0s;\r\n}\r\n.nav-item:nth-child(1):before {\r\n  transition-delay: 0s;\r\n}\r\n.nav-item:nth-child(2) {\r\n  transition-delay: 0.05s;\r\n}\r\n.nav-item:nth-child(2):before {\r\n  transition-delay: 0.05s;\r\n}\r\n.nav-item:nth-child(3) {\r\n  transition-delay: 0.1s;\r\n}\r\n.nav-item:nth-child(3):before {\r\n  transition-delay: 0.1s;\r\n}\r\n.nav-item:nth-child(4) {\r\n  transition-delay: 0.15s;\r\n}\r\n.nav-item:nth-child(4):before {\r\n  transition-delay: 0.15s;\r\n}\r\n.nav-item:nth-child(1) {\r\n  letter-spacing: -8px;\r\n}\r\n.nav-item:nth-child(2) {\r\n  letter-spacing: -7px;\r\n}\r\n.nav-item:nth-child(n + 4) {\r\n  letter-spacing: -8px;\r\n  margin-top: -7px;\r\n  opacity: 0;\r\n}\r\n.nav-item:before {\r\n  position: absolute;\r\n  content: \'\';\r\n  top: 50%;\r\n  left: 0;\r\n  width: 100%;\r\n  height: 2px;\r\n  background-color: #EC7263;\r\n  transform: translateY(-50%) scaleY(5);\r\n  transition: 0.5s;\r\n}\r\n\r\n/* Dummy content */\r\n.dummy-content {\r\n  position: relative;\r\n  text-align: center;\r\n  transition: 0.5s;\r\n}\r\n.dummy-content:before {\r\n  position: absolute;\r\n  content: \'\';\r\n  top: 0;\r\n  left: 0;\r\n  width: 100%;\r\n  height: 100%;\r\n  background-color: transparent;\r\n  z-index: 2;\r\n  transition: 0.5s;\r\n}\r\n\r\n.circle {\r\n  display: inline-block;\r\n  width: 75px;\r\n  height: 75px;\r\n  background-color: #EC7263;\r\n  border-radius: 100%;\r\n}\r\n\r\n.text {\r\n  margin: 15px 0 30px;\r\n}\r\n.text span {\r\n  display: inline-block;\r\n  height: 10px;\r\n  margin: 0 5px;\r\n  background-color: #C06162;\r\n  border-radius: 5px;\r\n}\r\n.text span:first-child {\r\n  width: 50px;\r\n}\r\n.text span:last-child {\r\n  width: 80px;\r\n}\r\n\r\n.square-top {\r\n  display: inline-block;\r\n  position: relative;\r\n  width: 200px;\r\n  height: 300px;\r\n  background-color: #FEBE7E;\r\n  z-index: 1;\r\n}\r\n\r\n.square-behind {\r\n  display: inline-block;\r\n  position: relative;\r\n  top: -256px;\r\n  width: 250px;\r\n  height: 210px;\r\n  background-color: #C28683;\r\n}\r\n.square-behind:before, .square-behind:after {\r\n  position: absolute;\r\n  content: \'\';\r\n  top: 0;\r\n  width: 40%;\r\n  height: 100%;\r\n}\r\n.square-behind:before {\r\n  left: 0;\r\n  background-color: #9D567C;\r\n}\r\n.square-behind:after {\r\n  right: 0;\r\n  background-color: #958C6B;\r\n}\r\n',	'',	'',	'',	1,	'2017-01-11 05:09:43'),
(4,	'HSL Color Picker',	'<section id=\"picker\">\r\n  <h1>HSL-picker</h1>\r\n  <input type=\"range\" min=\"0\" step=\"1\" max=\"360\" id=\"hsl-h\">\r\n  <input type=\"range\" min=\"0\" step=\"1\" max=\"100\" id=\"hsl-s\">\r\n  <input type=\"range\" min=\"0\" step=\"1\" max=\"100\" id=\"hsl-l\">\r\n  <div id=\"sample\"></div>\r\n</section>',	'',	'* {\r\n  box-sizing: border-box;\r\n}\r\n\r\n:root {\r\n  font-family: sans-serif;\r\n}\r\n\r\nbody {\r\n  margin: 0;\r\n}\r\n\r\n#picker {\r\n  background-color: white;\r\n  box-shadow: 0 .5rem 1rem -.25rem lightgray;\r\n  margin: 32px 0 16px;\r\n  margin: 2rem 0 1rem;\r\n  padding: 16px;\r\n  padding: 1rem;\r\n}\r\n\r\n#picker h1 {\r\n  font-weight: 100;\r\n  line-height: 1;\r\n  margin: 0 0 16px;\r\n  margin: 0 0 1rem;\r\n  color: gray;\r\n}\r\n\r\n[type=\"range\"] {\r\n  -webkit-appearance: none;\r\n  -moz-appearance: none;\r\n       appearance: none;\r\n  background-color: currentColor;\r\n  background-origin: content-box;\r\n  background-repeat: no-repeat;\r\n  display: block;\r\n  color: inherit;\r\n  height: 48px;\r\n  height: 3rem;\r\n  margin: 0 0 16px;\r\n  margin: 0 0 1rem;\r\n  padding: 0;\r\n  width: 100%;\r\n}\r\n[type=\"range\"]:focus {\r\n  outline: none;\r\n}\r\n[type=\"range\"]::-moz-range-track {\r\n  background-color: transparent;\r\n}\r\n[type=\"range\"]::-webkit-slider-runnable-track {\r\n  margin: 0 -16px;\r\n  margin: 0 -1rem;\r\n}\r\n[type=\"range\"]::-webkit-slider-thumb {\r\n  -webkit-appearance: none;\r\n          appearance: none;\r\n  background-color: transparent;\r\n  border-radius: 100%;\r\n  box-shadow:\r\n    inset 0 0 0 .125rem currentColor,\r\n    inset 0 0 0 .25rem white;\r\n  height: 32px;\r\n  height: 2rem;\r\n  width: 32px;\r\n  width: 2rem;\r\n}\r\n[type=\"range\"]::-moz-range-thumb {\r\n  background-color: transparent;\r\n  border: 0;\r\n  border-radius: 100%;\r\n  box-shadow:\r\n    inset 0 0 0 .125rem currentColor,\r\n    inset 0 0 0 .25rem white;\r\n  height: 32px;\r\n  height: 2rem;\r\n  width: 32px;\r\n  width: 2rem;\r\n}\r\n#sample {\r\n  height: 48px;\r\n  height: 3rem;\r\n  text-align: right;\r\n  padding: 4px;\r\n  padding: .25rem;\r\n  width: 100%;\r\n}\r\n#sample div {\r\n  cursor: pointer;\r\n  font-size: 12px;\r\n  font-size: .75rem;\r\n  font-weight: 100;\r\n  line-height: 1.5em;\r\n}\r\n#sample div:hover {\r\n  text-decoration: underline;\r\n}\r\n#sample .white {\r\n  color: white;\r\n}\r\n#sample .black {\r\n  color: black;\r\n}\r\n\r\n#hsl-h {\r\n  background-image: -webkit-linear-gradient(\r\n    left,\r\n    #F00,\r\n    #FF0,\r\n    #0F0,\r\n    #0FF,\r\n    #00F,\r\n    #F0F,\r\n    #F00\r\n  );\r\n  background-image: linear-gradient(\r\n    to right,\r\n    #F00,\r\n    #FF0,\r\n    #0F0,\r\n    #0FF,\r\n    #00F,\r\n    #F0F,\r\n    #F00\r\n  );\r\n}\r\n\r\n#hsl-s {\r\n  background-image: -webkit-linear-gradient(\r\n    left,\r\n    #888,\r\n    transparent\r\n  );\r\n  background-image: linear-gradient(\r\n    to right,\r\n    #888,\r\n    transparent\r\n  );\r\n}\r\n#hsl-l {\r\n  background-image: -webkit-linear-gradient(\r\n    left,\r\n    #000,\r\n    transparent,\r\n    #FFF\r\n  );\r\n  background-image: linear-gradient(\r\n    to right,\r\n    #000,\r\n    transparent,\r\n    #FFF\r\n  );\r\n}\r\n\r\n@media screen and (min-width: 42rem) {\r\n  #picker {\r\n    width: 42rem;\r\n    margin: 2rem auto 1rem;\r\n  }\r\n}\r\n\r\n',	'',	'var picker = {\r\n  container: document.getElementById(\"picker\"),\r\n  sample: document.getElementById(\"sample\")\r\n};\r\n\r\nvar colors = {\r\n  h: 320,\r\n  s: 80,\r\n  l: 50,\r\n  textColor: function() {\r\n    return this.l > 42 ?\r\n      \"black\" :\r\n      \"white\";\r\n  },\r\n  hueValue: function() {\r\n    return Math.floor(this.h);\r\n  },\r\n  satValue: function() {\r\n    return Math.floor(this.s) + \"%\";\r\n  },\r\n  lumValue: function() {\r\n    return Math.floor(this.l) + \"%\";\r\n  },\r\n  getHSLString: function() {\r\n    return \"hsl(\" + [\r\n      colors.hueValue(),\r\n      colors.satValue(),\r\n      colors.lumValue()\r\n    ].join(\", \") + \")\";\r\n  },\r\n  getHueLumString: function() {\r\n    return \"hsl(\" +\r\n      colors.hueValue() + \", 100%, \" +\r\n      colors.lumValue() + \")\";\r\n  },\r\n  getHueSatString: function() {\r\n    return \"hsl(\" +\r\n      colors.hueValue() + \", \" +\r\n      colors.satValue() + \", 50%)\";\r\n  }\r\n};\r\n\r\npicker.sliders = Array.from(\r\n  picker.container.querySelectorAll(\"input[type=\'range\']\")\r\n);\r\n\r\npicker.sliders.forEach(function(p, i) {\r\n  p.addEventListener(\"input\", handleSliderChange);\r\n  initSlider(p);\r\n});\r\n\r\nfunction handleSliderChange() {\r\n  var sliderType = this.id.split(\"hsl-\")[1];\r\n  if(sliderType === \"h\") {\r\n    colors.h = this.value;\r\n  } else if(sliderType === \"s\") {\r\n    colors.s = this.value;\r\n  } else if(sliderType === \"l\") {\r\n    colors.l = this.value;\r\n  }\r\n  updateColors();\r\n}\r\n\r\nfunction fixChromeRepaintIssue(event) {\r\n  var updateable = picker.sliders;\r\n  if(event.type !== \"mouseup\") {\r\n     updateable = picker.sliders.filter(function(el) {\r\n      return el !== event.target;\r\n    });\r\n  }\r\n  updateable.forEach(forceRepaint)\r\n}\r\n\r\nfunction forceRepaint(element) {  \r\n  element.style.display=\'none\';\r\n  element.offsetHeight;\r\n  element.style.display=\'\';\r\n}\r\n\r\nfunction updateColors() {\r\n  picker.sliders.forEach(updateColor);\r\n}\r\n\r\nfunction updateColor(color) {\r\n  picker.sliders[0].style.color = colors.getHSLString();\r\n  picker.sliders[1].style.color = colors.getHueLumString();\r\n  picker.sliders[2].style.color = colors.getHueSatString();\r\n  picker.sample.style.backgroundColor = colors.getHSLString();\r\n  picker.sample.innerHTML = getColorValuesHTML();\r\n}\r\n\r\nfunction getColorValuesHTML() {\r\n  return [\r\n    \"<div class=\\\"\" + colors.textColor() + \"\\\">\",\r\n    [\r\n      colors.getHSLString(),\r\n      window.getComputedStyle(picker.sliders[0]).color\r\n    ].join(\"</div><div class=\\\"\" + colors.textColor() + \"\\\">\"),\r\n    \"</div>\"\r\n  ].join(\"\");\r\n}\r\n\r\nfunction initSlider(s) {\r\n  if(s.id===\"hsl-h\") {\r\n    s.value = colors.h;\r\n  } else if(s.id ===\"hsl-s\") {\r\n    s.value = colors.s;\r\n  } else if(s.id ===\"hsl-l\") {\r\n    s.value = colors.l;\r\n  }\r\n  updateColors();\r\n}',	'',	1,	'2017-01-11 05:09:43'),
