(6,	'Button with Hover Effect',	'<script src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/3.1.0/jquery.min.js\"></script>\r\n<div class=\"container\">\r\n  <div class=\"title\">\r\n    <a href=\"http://codepen.io/sashatran/\" target=\"_blank\">Subscribe Now</a>\r\n    </div>\r\n</div>',	'',	'@import url(\"https://fonts.googleapis.com/css?family=Roboto\");\r\n* {\r\n  padding: 0;\r\n  margin: 0;\r\n}\r\n\r\n.container {\r\n  height: 100vh;\r\n  display: flex;\r\n  justify-content: center;\r\n  align-items: center;\r\n  background: linear-gradient(50deg, #B388EB, #8093F1);\r\n  opacity: 0.8;\r\n  filter: saturate(1.6);\r\n}\r\n\r\n.title {\r\n  width: 200px;\r\n  height: 20px;\r\n  font-size: 16px;\r\n  font-family: \'Roboto\', sans-serif;\r\n  color: #FFF;\r\n  text-transform: uppercase;\r\n  text-align: center;\r\n  letter-spacing: 4px;\r\n  background: rgba(0, 0, 0, 0.2);\r\n  padding: 15px 20px;\r\n  transition: all .5s ease;\r\n  border-radius: 3px;\r\n  cursor: pointer;\r\n}\r\n.title a {\r\n  text-decoration: none;\r\n  color: #FFF;\r\n}\r\n\r\n.is-active {\r\n  box-shadow: 0 20px 5px -10px rgba(0, 0, 0, 0.4);\r\n  transform: translateY(10px);\r\n}\r\n',	'',	'$(\".title\").hover(function(){\r\n  $(this).toggleClass(\"is-active\");\r\n})\r\n\r\n//Without JS by Ana Tudor\r\n// https://codepen.io/anon/pen/egGKwv\r\n\r\n// by Scott\r\n//Button to zoom in instead of moving down\r\n// http://codepen.io/scottjenson/pen/mRBGbP',	'',	1,	'2017-01-21 05:09:43'),
(7,	'CSS SKULL',	'<div class=\"container\">\r\n  <div class=\"skull\">\r\n    <div class=\"eye\"></div>\r\n    <div class=\"eye\"></div>\r\n    <div class=\"nose\"></div>\r\n  </div>\r\n  <div class=\"teeth\">\r\n  <div class=\"tooth\"></div>\r\n  <div class=\"tooth\"></div>\r\n  <div class=\"tooth\"></div>\r\n  <div class=\"tooth\"></div>\r\n  </div>\r\n  <div class=\"bones\">\r\n    <div class=\"bone\">\r\n      <div class=\"bone-inner\"></div>\r\n      <div class=\"bone-inner\"></div>\r\n    </div>\r\n    <div class=\"bone\">\r\n      <div class=\"bone-inner\"></div>\r\n      <div class=\"bone-inner\"></div>\r\n    </div>\r\n  </div>\r\n</div>',	'',	'*,\r\n*:before,\r\n*:after {\r\n  box-sizing: border-box;\r\n  background-clip: padding-box;\r\n}\r\n\r\n.container {\r\n  position: absolute;\r\n  top: 45%;\r\n  left: 50%;\r\n  transform: translate(-50%, -50%);\r\n}\r\n\r\n.skull {\r\n  position: relative;\r\n  width: 16em;\r\n  height: 10em;\r\n  border: solid .5em black;\r\n  border-radius: 5em 5em 3em 3em;\r\n  background: white;\r\n  z-index: 4;\r\n}\r\n\r\n.teeth {\r\n  position: absolute;\r\n  bottom: -2.75em;\r\n  left: 2.5em;\r\n  z-index: 7;\r\n  text-align: center;\r\n}\r\n\r\n.tooth {\r\n  display: inline-block;\r\n  margin-left: -.55em;\r\n  width: 3em;\r\n  height: 3em;\r\n  background: white;\r\n  border: solid .5em black;\r\n  border-top-width: 0;\r\n  border-radius: 0 0 1em 1em;\r\n}\r\n\r\n.tooth:first-child {\r\n  margin-left: 0;\r\n}\r\n\r\n.eye {\r\n  position: absolute;\r\n  top: 4em;\r\n  left: 2em;\r\n  width: 4em;\r\n  height: 2.5em;\r\n  background: black;\r\n  border: solid .5em black;\r\n  border-radius: .5em .5em 2em 2em;\r\n  animation: rainbow .5s infinite;\r\n}\r\n\r\n.eye + .eye {\r\n  left: auto;\r\n  right: 2em;\r\n}\r\n\r\n.nose {\r\n  position: absolute;\r\n  bottom: .5em;\r\n  left: 50%;\r\n  margin-left: -1em;\r\n  width: 2em;\r\n  height: 2em;\r\n  border-top: solid .5em black;\r\n  border-left: solid .5em black;\r\n  border-right: solid .5em transparent;\r\n  border-bottom: solid .5em transparent;\r\n  border-radius: .5em;\r\n  transform: rotate(45deg);\r\n}\r\n\r\n.bones {\r\n  transform: rotate(-45deg) translate(.75em, -4em);\r\n}\r\n\r\n.bone {\r\n  position: absolute;\r\n  width: 20em;\r\n  height: 3em;\r\n  border-top: solid .5em black;\r\n  border-bottom: solid .5em black;\r\n  background: white;\r\n  z-index: 1;\r\n}\r\n\r\n.bone + .bone {\r\n  transform: rotate(90deg);\r\n}\r\n\r\n.bone-inner:after,\r\n.bone-inner:before {\r\n  content: \'\';\r\n  position: absolute;\r\n  top: -1.5em;\r\n  left: -1.5em;\r\n  width: 3em;\r\n  height: 3em;\r\n  border: solid .5em black;\r\n  border-right-color: white;\r\n  border-radius: 50%;\r\n  background: white;\r\n  transform: rotate(45deg);\r\n}\r\n\r\n.bone-inner:after {\r\n  top: auto;\r\n  bottom: -1.5em;\r\n  left: -1.5em;\r\n  transform: rotate(-45deg);\r\n}\r\n\r\n.bone-inner + .bone-inner:before {\r\n  left: auto;\r\n  right: -1.5em;\r\n  transform: rotate(135deg);\r\n}\r\n\r\n.bone-inner + .bone-inner:after {\r\n  left: auto;\r\n  right: -1.5em;\r\n  transform: rotate(-135deg);\r\n}\r\n\r\n@keyframes rainbow {\r\n  0% {\r\n    background: red;\r\n  }\r\n  25% {\r\n    background: blue;\r\n  }\r\n  50% {\r\n    background: green;\r\n  }\r\n  75% {\r\n    background: yellow;\r\n  }\r\n  100% {\r\n    background: red;\r\n  }\r\n}\r\n\r\nhtml,\r\nbody {\r\n  width: 100%;\r\n  height: 100%;\r\n  margin: 0;\r\n}\r\n\r\nhtml {\r\n  font-size: 3vmin;\r\n}\r\n\r\nbody {\r\n  background: linear-gradient(to bottom, HotPink, salmon);\r\n  overflow: hidden;\r\n}',	'',	'',	'',	1,	'2017-01-21 05:09:43');
